    function _getEligibleWinners(uint256 round) internal view returns (address[] memory) {
        address[] storage participants = _participants[round];
        address loser = rounds[round].loser;

        uint256 count = 0;
        for (uint256 i = 0; i < participants.length; i++) {
            if (participants[i] != loser) count++;
        }

        address[] memory winners = new address[](count);
        uint256 idx = 0;
        for (uint256 i = 0; i < participants.length; i++) {
            if (participants[i] != loser) {
                winners[idx++] = participants[i];
            }
        }
        return winners;
